package com.example.myyoutubechik.uiimport androidx.lifecycle.LiveDataimport com.example.myyoutubechik.core.network.result.Resourceimport com.example.myyoutubechik.core.ui.BaseViewModelimport com.example.myyoutubechik.data.remote.model.PlaylistItemimport com.example.myyoutubechik.data.remote.model.Videosimport com.example.myyoutubechik.repositories.Repositoryclass MainViewModel(private val repository: Repository) : BaseViewModel() {    fun getPlaylists(): LiveData<Resource<PlaylistItem>> {        return repository.getPlaylists()    }    fun getPlaylistItem(playlistId: String?): LiveData<Resource<PlaylistItem>> {        return repository.getPlaylistItem(playlistId!!)    }    fun getVideo(id: String): LiveData<Resource<Videos>> {        return repository.getVideo(id)    }}