package com.example.myyoutubechik.ui.detailimport android.annotation.SuppressLintimport android.os.Buildimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.annotation.RequiresApiimport androidx.recyclerview.widget.RecyclerViewimport coil.loadimport com.example.myyoutubechik.data.remote.model.PlaylistItemimport com.example.myyoutubechik.databinding.ItemDetailBindingclass DetailAdapter(private val onClick: (PlaylistItem.Item) -> Unit) :    RecyclerView.Adapter<DetailAdapter.DetailViewHolder>() {    private var playlistItems = listOf<PlaylistItem.Item>()    @SuppressLint("NotifyDataSetChanged")    fun addList(list: List<PlaylistItem.Item?>?) {        this.playlistItems = list as List<PlaylistItem.Item>        notifyDataSetChanged()    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): DetailViewHolder {        return DetailViewHolder(            ItemDetailBinding.inflate(LayoutInflater.from(parent.context), parent, false)        )    }    override fun getItemCount(): Int {        return playlistItems.size    }    @RequiresApi(Build.VERSION_CODES.O)    override fun onBindViewHolder(holder: DetailViewHolder, position: Int) {        val playlistItem = playlistItems[position]        holder.onBind(playlistItem)    }    inner class DetailViewHolder(private val binding: ItemDetailBinding) :        RecyclerView.ViewHolder(binding.root) {        @RequiresApi(Build.VERSION_CODES.O)        fun onBind(item: PlaylistItem.Item) {            binding.image.load(item.snippet?.thumbnails?.standard?.url!!)            binding.titleTv.text = item.snippet.title            binding.cvImage.setOnClickListener {                onClick.invoke(item)            }        }    }}