package com.example.myyoutubechik.ui.playlistimport android.annotation.SuppressLintimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport androidx.recyclerview.widget.RecyclerView.ViewHolderimport coil.loadimport com.example.myyoutubechik.data.remote.model.PlaylistItemimport com.example.myyoutubechik.databinding.ItemPlaylistBindingclass PlayListAdapter(private val onClick: (PlaylistItem.Item) -> Unit) :    RecyclerView.Adapter<PlayListAdapter.PlayListViewHolder>() {    private var list = arrayListOf<PlaylistItem.Item>()    @SuppressLint("NotifyDataSetChanged")    fun addList(list: List<PlaylistItem.Item?>?) {        this.list = list as ArrayList<PlaylistItem.Item>        notifyDataSetChanged()    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): PlayListViewHolder {        return PlayListViewHolder(            ItemPlaylistBinding.inflate(                LayoutInflater.from(parent.context), parent, false            )        )    }    override fun onBindViewHolder(holder: PlayListViewHolder, position: Int) {        holder.onBind(list[position])    }    override fun getItemCount(): Int {        return list.size    }    inner class PlayListViewHolder(private val binding: ItemPlaylistBinding) :        ViewHolder(binding.root) {        fun onBind(item: PlaylistItem.Item) {            binding.imgPlay.load(item.snippet?.thumbnails?.standard?.url)            binding.tvTitle.text = item.snippet?.title            binding.tvCount.text = item.contentDetails?.itemCount.toString() + " video"            binding.cvImage.setOnClickListener {                onClick.invoke(item)            }        }    }}